mapper.select:
SELECT a.* FROM cw_base_camera a
and a.cameraType = #{cameraType}
and a.cameraName LIKE CONCAT('%', #{cameraName}, '%')
and a.cameraType in
#{item}
AND a.state = 0

mapper.update:
UPDATE cw_base_camera
cameraName = #{cameraName}, orgId = #{orgId}, sceneId = #{sceneId}, installAdd = #{installAdd}, streamSource = #{streamSource}, lon = #{lon}, lat = #{lat}, updateTime = NOW()
WHERE id = #{id}

mapper.select:
SELECT a.id, case when a.standardCameraId is not null and a.standardCameraId != '' then a.standardCameraId else a.cameraId end cameraId, a.cameraName, a.cameraType, e1.enumName AS cameraTypeName, a.installAdd, a.monitorStatus, e2.enumName AS monitorStatusName, a.cameraState, e3.enumName AS cameraStateName, a.cameraEnabled, e4.enumName AS cameraEnabledName, a.orgId, o.org_cname AS orgName FROM cw_base_camera a LEFT JOIN cw_ge_organization o ON a.orgId = o.org_id LEFT JOIN cw_base_enum e1 ON e1.enumType = 'CAMERA_TYPE' AND a.cameraType = e1.enumValue LEFT JOIN cw_base_enum e2 ON e2.enumType = 'FACEDB_MONITOR_STATE' AND a.monitorStatus = e2.enumValue LEFT JOIN cw_base_enum e3 ON e3.enumType = 'CAMERA_STATE' AND a.cameraState = e3.enumValue LEFT JOIN cw_base_enum e4 ON e4.enumType = 'ENABLED_STATUS' AND a.cameraEnabled = e4.enumValue
a.state = 0
AND a.cameraName LIKE CONCAT('%',#{cameraName},'%')
AND case when a.standardCameraId is not null and a.standardCameraId != '' then a.standardCameraId else a.cameraId end LIKE CONCAT('%',#{cameraId},'%')
AND a.cameraType = #{cameraType}
AND a.orgId = #{orgId}
AND a.cameraState = #{cameraState}
AND a.cameraEnabled = #{cameraEnabled}
AND a.id in
#{item,jdbcType=INTEGER}
ORDER BY a.cameraId DESC

SELECT
*
FROM
cw_base_camera


        cameraId = #{cameraId}


        AND cameraName = #{cameraName}


        AND cameraType = #{cameraType}


        AND cameraState = #{cameraState}


LIMIT 0,1

SELECT
cameraId
FROM
cw_base_camera t1
WHERE
t1.state = 0

    AND t1.cameraType = #{cameraType}


    AND ${sqlString}


    AND t1.cameraType in

        #{item}

SELECT cameraId FROM cw_base_camera
state = 0
AND cameraType = #{cameraType}
AND cameraType IN
#{item}
AND FIND_IN_SET(villageCode,#{villageCode})

SELECT * FROM cw_base_camera
state = 0 AND cameraIp = #{cameraIp}
OR standardCameraId = #{standardCameraId}
LIMIT 0,1

SELECT * FROM cw_base_camera
state != 1
AND id != #{id}
AND cameraIp = #{cameraIp}
LIMIT 0,1

update.foreach:
UPDATE cw_base_camera
SET cameraState = #{item.cameraState}
WHERE
id = #{item.id}
