LINK_MAN_EMAIL, DELETED_TIME, DELETED_USERID, FROM_SYSTEM, IS_LINKAGE
from CW_GE_ORGANIZATION
where 1 = 1



		and STATUS = #{status, jdbcType=INTEGER}


